import React from 'react';

import { Table } from 'unchained-ui-react';

const tableData = [
  { name: undefined, status: undefined, notes: undefined },
  { name: 'Shwetha', status: 'Requires Action', notes: undefined },
  { name: 'Nishma', status: undefined, notes: 'Hostile' },
  { name: 'Komal', status: undefined, notes: undefined },
];

const headerRow = [
  'Name',
  'Status',
  'Notes',
];

const renderBodyRow = ({ name, status, notes }, i) => ({
  key: name || `row-${i}`,
  warning: !!(status && status.match('Requires Action')),
  cells: [
    name || 'No name specified',
    status
      ? { key: 'status', icon: 'attention', content: status }
      : 'Unknown',
    notes
      ? { key: 'notes', icon: 'attention', content: notes, warning: true }
      : 'None',
  ],
});

const TableExampleWarningShorthand = () => (
  <Table
    celled
    headerRow={headerRow}
    renderBodyRow={renderBodyRow}
    tableData={tableData}
  />
);

export default TableExampleWarningShorthand;
