import React, { Component } from 'react';

import { Button, Divider, Embed } from 'unchained-ui-react';

export default class EmbedExampleActive extends Component {
  state = {};

  handleClick = () => this.setState({ active: true });

  render() {
    const { active } = this.state;

    return (
      <div>
        <Embed
          active={active}
          icon={'arrow circle down'}
          id={'W_B2UZ_ZoxU'}
          placeholder={'/assets/images/embed/youtube_W_B2UZ_ZoxU.jpg'}
          source={'youtube'}
        />

        <Divider hidden />

        <Button
          content={'Activate'}
          icon={'bomb'}
          labelPosition={'left'}
          onClick={this.handleClick}
        />
      </div>
    );
  }
}
