import React from 'react';
import { Image, Popup } from 'unchained-ui-react';

const users = [
  {
    name: 'Mevin',
    bio: 'Mevin has been a member since July 2012',
    avatar: '/assets/images/avatar/mevin.jpg',
  },
  {
    name: 'Sudarshan',
    bio: 'Sudarshan has been a member since August 2013',
    avatar: '/assets/images/avatar/sudarshan.jpg',
  },
  {
    name: 'Vindhya',
    bio: 'Vindhya has been a member since July 2014',
    avatar: '/assets/images/avatar/vindhya.jpg',
  },
];

export default function PopupExampleTitled() {
  const avatars = users.map((user) =>
    <Popup
      key={user.name}
      trigger={<Image src={user.avatar} avatar />}
      header={user.name}
      content={user.bio}
    />
  );

  return <div>{avatars}</div>;
}
