import React from 'react';
import PropTypes from 'prop-types';

import {
  Popup
} from 'src/components';

const toolTipStyle = {
  width: 100,
  textAlign: 'center',
  padding: '0.5em'
};

const ToolTip = ({ children, content }) => (
  <Popup
    inverted
    mouseEnterDelay={800}
    position={'top center'}
    size={'tiny'}
    style={toolTipStyle}
    trigger={children}
    content={content}
  />
);

ToolTip.propTypes = {
  children: PropTypes.node,
  content: PropTypes.node,
};

export default ToolTip;
