import React from 'react';
import PropTypes from 'prop-types';
import { NavLink } from 'react-router-dom';

import {
  Container,
  Icon,
  Menu,
} from 'src/components';

import {
  repoURL,
} from 'docs/app/utils';

import {
  Logo,
} from 'docs/app/components';

const AppTopbar = (props) => {
  const {
    toggleSidebar,
  } = props;

  return (
    <Menu fixed={'top'} inverted>
      <Container>
        <Menu.Item as={NavLink} to={'/intro'} activeClassName={''}>
          <Logo spaced={'right'} size={'mini'} />
        </Menu.Item>

        <Menu.Item name={'Menu'} onClick={toggleSidebar}>
          <Icon name={'sidebar'} />
          {'Menu'}
        </Menu.Item>

        <Menu.Menu position={'right'}>
          <Menu.Item as={'a'} href={repoURL}>
            <Icon name={'github'} />
            {'Github'}
          </Menu.Item>
        </Menu.Menu>
      </Container>
    </Menu>
  );
};

AppTopbar.propTypes = {
  toggleSidebar: PropTypes.func.isRequired,
};

export default AppTopbar;
