import React from 'react';
import {
  BrowserRouter,
  Route,
  Redirect,
  Switch,
} from 'react-router-dom';

import Layout from './components/Layout';

import {
  DocPage,
  IntroPage,
} from './components';

const RedirectToIntro = () => <Redirect to={'intro'} />;

const Router = () => (
  <BrowserRouter>
    <Switch>
      <Layout>
        <Switch>
          <Route exact path={'/'} render={RedirectToIntro} />
          <Route exact path={'/intro'} component={IntroPage} />
          <Route exact path={'/:type/:name'} component={DocPage} />
        </Switch>
      </Layout>
    </Switch>
  </BrowserRouter>
);

export default Router;
