import cx from 'classnames';
import React from 'react';
import PropTypes from 'prop-types';

import {
  createShorthandFactory,
  getUnhandledProps,
  META,
  UV5UI,
  useVerticalAlignProp,
} from '../../../lib';

import Icon from '../../media/Icon';

/**
 * A list item can contain an icon.
 */
const ListIcon = (props) => {
  const {
    className,
    verticalAlign
  } = props;

  const classes = cx(
    useVerticalAlignProp(verticalAlign),
    className
  );

  const rest = getUnhandledProps(ListIcon, props);

  return <Icon {...rest} className={classes} />;
};

ListIcon._meta = {
  name: 'ListIcon',
  parent: 'List',
  type: META.TYPES.CONTAINER,
};

ListIcon.propTypes = {
  /** Additional classes. */
  className: PropTypes.string,

  /** An element inside a list can be vertically aligned. */
  verticalAlign: PropTypes.oneOf(UV5UI.VERTICAL_ALIGNMENTS),
};

ListIcon.create = createShorthandFactory(ListIcon, name => ({ name }));

export default ListIcon;
